import React, { Component } from 'react'
import './ParallaxDiv1.css'

const nystories = document.querySelector("p").offsetTop;
window.onscroll = function () {
  if (window.pageYOffset > 0) {
    var opac = (window.pageYOffset / nystories);
    console.log(opac);
    document.body.style.background = "linear-gradient(rgba(255, 255, 255, " + opac + "), rgba(0, 0, 0, " + opac + ")), url(https://s3-us-west-2.amazonaws.com/s.cdpn.io/4273/times-square-perspective.jpg) no-repeat";
  }
}

export default class ParallaxDiv1 extends Component {
  render() {
    return (
      <div className='parallax1-background' />
    )
  }
}
